mapper.select:
select a.* from article a join article_category ac on a.id=ac.article_id where ac.category_id=#{cateId} and a.status in
#{status}
and a.encrypt_enable=false order by a.updated desc

select a.* from article a join article_category ac on a.id=ac.article_id where art.tag_Id=#{tagId} and a.status in
#{status}
and a.encrypt_enable=false order by a.updated desc

select a.* from article a where a.status in
#{status}
and a.encrypt_enable=false order by a.updated desc
